              animate={{ opacity: 1 }}
              className="space-y-6"
            >
              {/* Filters */}
              <div className="flex flex-wrap gap-4">
                <select
                  value={filterStatus}
                  onChange={(e) => setFilterStatus(e.target.value)}
                  className="px-4 py-2 border border-teal-200 rounded-lg focus:ring-2 focus:ring-teal-500"
